// src/pages/DashboardPage.js
import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Table, ProgressBar, Badge } from 'react-bootstrap';
import { motion } from 'framer-motion';
import { 
  BarChartFill, 
  ImageFill, 
  GearWideConnected, 
  CheckCircle, 
  Clock, 
  GraphUp 
} from 'react-bootstrap-icons';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const DashboardPage = () => {
  const { currentUser, isAuthenticated, loading } = useAuth();
  const navigate = useNavigate();
  const [dashboardData, setDashboardData] = useState({
    totalDetections: 0,
    detectionHistory: [],
    topLabels: []
  });

  useEffect(() => {
    if (!loading && !isAuthenticated) {
      navigate('/login');
    }
  }, [loading, isAuthenticated, navigate]);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        // In real backend: const response = await axios.get('/api/dashboard');
        await new Promise(resolve => setTimeout(resolve, 800)); // mock loading

        setDashboardData({
          totalDetections: 42,
          detectionHistory: [
            { id: 1, image: 'image1.jpg', objects: 3, timestamp: '2025-10-07T10:30:00Z', status: 'completed' },
            { id: 2, image: 'image2.jpg', objects: 5, timestamp: '2025-10-06T09:15:00Z', status: 'completed' },
            { id: 3, image: 'image3.jpg', objects: 2, timestamp: '2025-10-05T16:45:00Z', status: 'completed' },
            { id: 4, image: 'image4.jpg', objects: 7, timestamp: '2025-10-04T14:20:00Z', status: 'completed' },
            { id: 5, image: 'image5.jpg', objects: 1, timestamp: '2025-10-03T11:55:00Z', status: 'completed' }
          ],
          topLabels: [
            { label: 'person', count: 28 },
            { label: 'car', count: 15 },
            { label: 'dog', count: 8 },
            { label: 'bicycle', count: 6 },
            { label: 'chair', count: 5 }
          ]
        });
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
      }
    };

    fetchDashboardData();
  }, []);

  const getStatusBadge = (status) => {
    switch (status) {
      case 'completed': return <Badge bg="success">Completed</Badge>;
      case 'processing': return <Badge bg="warning">Processing</Badge>;
      case 'failed': return <Badge bg="danger">Failed</Badge>;
      default: return <Badge bg="secondary">{status}</Badge>;
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <Container>
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <Row className="mb-4">
          <Col>
            <h1 className="fw-bold">Dashboard</h1>
            <p className="text-muted">Welcome back, {currentUser?.name || 'User'}! Here’s your activity overview.</p>
          </Col>
        </Row>

        {/* Stats Cards */}
        <Row className="mb-4">
          <Col md={4} className="mb-3">
            <Card className="text-center shadow-sm">
              <Card.Body>
                <BarChartFill size={40} className="text-primary mb-3" />
                <h3>{dashboardData.totalDetections}</h3>
                <p className="text-muted mb-0">Total Detections</p>
              </Card.Body>
            </Card>
          </Col>
          <Col md={4} className="mb-3">
            <Card className="text-center shadow-sm">
              <Card.Body>
                <ImageFill size={40} className="text-success mb-3" />
                <h3>12</h3>
                <p className="text-muted mb-0">3D Scenes Generated</p>
              </Card.Body>
            </Card>
          </Col>
          <Col md={4} className="mb-3">
            <Card className="text-center shadow-sm">
              <Card.Body>
                <GraphUp size={40} className="text-info mb-3" />
                <h3>98%</h3>
                <p className="text-muted mb-0">Accuracy Rate</p>
              </Card.Body>
            </Card>
          </Col>
        </Row>

        {/* Charts and Analytics */}
        <Row className="mb-4">
          <Col lg={6}>
            <Card className="shadow-sm">
              <Card.Header>
                <h5 className="mb-0">
                  <CheckCircle className="me-2" /> Top Detected Objects
                </h5>
              </Card.Header>
              <Card.Body>
                {dashboardData.topLabels.length > 0 ? (
                  dashboardData.topLabels.map((item, index) => (
                    <div key={index} className="mb-3">
                      <div className="d-flex justify-content-between mb-1">
                        <span className="fw-bold">{item.label}</span>
                        <span>{item.count}</span>
                      </div>
                      <ProgressBar 
                        now={(item.count / Math.max(...dashboardData.topLabels.map(i => i.count))) * 100} 
                        variant="primary"
                      />
                    </div>
                  ))
                ) : (
                  <p className="text-muted text-center my-4">No data available</p>
                )}
              </Card.Body>
            </Card>
          </Col>
          <Col lg={6}>
            <Card className="shadow-sm">
              <Card.Header>
                <h5 className="mb-0">
                  <Clock className="me-2" /> Recent Activity
                </h5>
              </Card.Header>
              <Card.Body>
                {dashboardData.detectionHistory.length > 0 ? (
                  <Table responsive>
                    <thead>
                      <tr>
                        <th>Image</th>
                        <th>Objects</th>
                        <th>Date</th>
                        <th>Status</th>
                      </tr>
                    </thead>
                    <tbody>
                      {dashboardData.detectionHistory.map((item) => (
                        <tr key={item.id}>
                          <td>{item.image}</td>
                          <td>{item.objects}</td>
                          <td>{new Date(item.timestamp).toLocaleDateString()}</td>
                          <td>{getStatusBadge(item.status)}</td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                ) : (
                  <p className="text-muted text-center my-4">No recent activity</p>
                )}
              </Card.Body>
            </Card>
          </Col>
        </Row>

        {/* Quick Actions */}
        <Row>
          <Col>
            <Card className="shadow-sm">
              <Card.Header>
                <h5 className="mb-0">
                  <GearWideConnected className="me-2" /> Quick Actions
                </h5>
              </Card.Header>
              <Card.Body>
                <div className="d-grid gap-2 d-md-flex justify-content-md-start">
                  <motion.div whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}>
                    <a href="/detection" className="btn btn-primary me-md-2 mb-2 mb-md-0">
                      <ImageFill className="me-2" /> Object Detection
                    </a>
                  </motion.div>
                  <motion.div whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}>
                    <a href="/3d-scene" className="btn btn-outline-primary mb-2 mb-md-0">
                      <GearWideConnected className="me-2" /> 3D Scene Mapping
                    </a>
                  </motion.div>
                  <motion.div whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}>
                    <a href="/features" className="btn btn-outline-secondary">
                      <BarChartFill className="me-2" /> Feature Demo
                    </a>
                  </motion.div>
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </motion.div>
    </Container>
  );
};

export default DashboardPage;
