import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Table, ProgressBar, Badge, Button } from 'react-bootstrap';
import { motion } from 'framer-motion';
import {
  BarChartFill,
  ImageFill,
  GearWideConnected,
  CheckCircle,
  Clock,
  GraphUp,
} from 'react-bootstrap-icons';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';

const DashboardPage = () => {
  const { currentUser, isAuthenticated, loading, logout } = useAuth();
  const navigate = useNavigate();

  const [dashboardData, setDashboardData] = useState({
    totalDetections: 0,
    detectionHistory: [],
    topLabels: [],
  });

  const [selectedFile, setSelectedFile] = useState(null);
  const [outputUrl, setOutputUrl] = useState('');

  // ✅ Authentication Guard
  useEffect(() => {
    if (!loading && !isAuthenticated) {
      navigate('/login');
    }
  }, [loading, isAuthenticated, navigate]);

  // ✅ Fetch Dashboard Data (mock data for now)
  useEffect(() => {
    const fetchDashboardData = async () => {
      await new Promise((resolve) => setTimeout(resolve, 500));
      setDashboardData({
        totalDetections: 42,
        detectionHistory: [
          { id: 1, image: 'image1.jpg', objects: 3, timestamp: '2025-10-07T10:30:00Z', status: 'completed' },
          { id: 2, image: 'image2.jpg', objects: 5, timestamp: '2025-10-06T09:15:00Z', status: 'completed' },
          { id: 3, image: 'image3.jpg', objects: 2, timestamp: '2025-10-05T16:45:00Z', status: 'completed' },
        ],
        topLabels: [
          { label: 'person', count: 28 },
          { label: 'car', count: 15 },
          { label: 'dog', count: 8 },
          { label: 'bicycle', count: 6 },
        ],
      });
    };
    fetchDashboardData();
  }, []);

  // ✅ Upload handler
  const handleFileChange = (e) => setSelectedFile(e.target.files[0]);

  // ✅ Detect Objects API
  const handleDetectBatch = async () => {
    if (!selectedFile) return alert('Please select an image first!');
    const formData = new FormData();
    formData.append('image', selectedFile);

    try {
      const res = await fetch('http://localhost:8000/detect-batch', {
        method: 'POST',
        body: formData,
      });
      const data = await res.json();
      setOutputUrl(data.output_url || '');
      alert('Detection complete!');
    } catch (err) {
      console.error('Detection Error:', err);
      alert('Error in detection API');
    }
  };

  // ✅ 3D Scene Map API
  const handle3DSceneMap = async () => {
    if (!selectedFile) return alert('Please select an image first!');
    const formData = new FormData();
    formData.append('image', selectedFile);

    try {
      const res = await fetch('http://localhost:8000/3d-scene-map', {
        method: 'POST',
        body: formData,
      });
      const data = await res.json();
      setOutputUrl(data.output_url || '');
      alert('3D Scene Map generated!');
    } catch (err) {
      console.error('3D Scene Error:', err);
      alert('Error in 3D Scene Map API');
    }
  };

  const getStatusBadge = (status) => {
    switch (status) {
      case 'completed':
        return <Badge bg="success">Completed</Badge>;
      case 'processing':
        return <Badge bg="warning">Processing</Badge>;
      case 'failed':
        return <Badge bg="danger">Failed</Badge>;
      default:
        return <Badge bg="secondary">{status}</Badge>;
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div
      style={{
        display: 'flex',
        height: '100vh',
        backgroundColor: '#f9fafb',
        overflow: 'hidden',
      }}
    >
      {/* Sidebar Navbar (20%) */}
      <div
        style={{
          width: '20%',
          backgroundColor: '#111827',
          color: 'white',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'space-between',
          padding: '30px 20px',
        }}
      >
        <div>
          <h3 className="fw-bold mb-4 text-center">Dashboard</h3>
          <input
            type="file"
            id="fileInput"
            style={{ display: 'none' }}
            onChange={handleFileChange}
          />
          <Button
            variant="primary"
            className="w-100 mb-3"
            onClick={() => document.getElementById('fileInput').click()}
          >
            Upload Image
          </Button>
          <Button variant="success" className="w-100 mb-3" onClick={handleDetectBatch}>
            Detect Objects
          </Button>
          <Button variant="info" className="w-100 mb-3" onClick={handle3DSceneMap}>
            3D Scene Map
          </Button>
        </div>
        <Button
          variant="danger"
          className="w-100"
          onClick={() => {
            logout();
            navigate('/login');
          }}
        >
          Logout
        </Button>
      </div>

      {/* Main Dashboard (80%) */}
      <div
        style={{
          width: '80%',
          overflowY: 'auto',
          padding: '40px',
        }}
      >
        <Container fluid>
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            <Row className="mb-4">
              <Col>
                <h1 className="fw-bold">Welcome, {currentUser?.name || 'User'}</h1>
                <p className="text-muted">
                  Manage detections and 3D scene mapping from here.
                </p>
              </Col>
              <Col className="text-end">
                <div
                  style={{
                    backgroundColor: '#2563eb',
                    color: 'white',
                    borderRadius: '50%',
                    padding: '10px 15px',
                    display: 'inline-block',
                  }}
                >
                  👤
                </div>
              </Col>
            </Row>

            {/* Stats Section */}
            <Row className="mb-4">
              <Col md={4}>
                <Card className="text-center shadow-sm">
                  <Card.Body>
                    <BarChartFill size={40} className="text-primary mb-3" />
                    <h3>{dashboardData.totalDetections}</h3>
                    <p className="text-muted mb-0">Total Detections</p>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={4}>
                <Card className="text-center shadow-sm">
                  <Card.Body>
                    <ImageFill size={40} className="text-success mb-3" />
                    <h3>12</h3>
                    <p className="text-muted mb-0">3D Scenes Generated</p>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={4}>
                <Card className="text-center shadow-sm">
                  <Card.Body>
                    <GraphUp size={40} className="text-info mb-3" />
                    <h3>98%</h3>
                    <p className="text-muted mb-0">Accuracy Rate</p>
                  </Card.Body>
                </Card>
              </Col>
            </Row>

            {/* Analytics + History */}
            <Row className="mb-4">
              <Col lg={6}>
                <Card className="shadow-sm">
                  <Card.Header>
                    <h5 className="mb-0">
                      <CheckCircle className="me-2" /> Top Detected Objects
                    </h5>
                  </Card.Header>
                  <Card.Body>
                    {dashboardData.topLabels.length ? (
                      dashboardData.topLabels.map((item, index) => (
                        <div key={index} className="mb-3">
                          <div className="d-flex justify-content-between mb-1">
                            <span className="fw-bold">{item.label}</span>
                            <span>{item.count}</span>
                          </div>
                          <ProgressBar
                            now={
                              (item.count /
                                Math.max(...dashboardData.topLabels.map((i) => i.count))) *
                              100
                            }
                            variant="primary"
                          />
                        </div>
                      ))
                    ) : (
                      <p className="text-muted text-center my-4">No data available</p>
                    )}
                  </Card.Body>
                </Card>
              </Col>

              <Col lg={6}>
                <Card className="shadow-sm">
                  <Card.Header>
                    <h5 className="mb-0">
                      <Clock className="me-2" /> Recent Activity
                    </h5>
                  </Card.Header>
                  <Card.Body>
                    {dashboardData.detectionHistory.length ? (
                      <Table responsive>
                        <thead>
                          <tr>
                            <th>Image</th>
                            <th>Objects</th>
                            <th>Date</th>
                            <th>Status</th>
                          </tr>
                        </thead>
                        <tbody>
                          {dashboardData.detectionHistory.map((item) => (
                            <tr key={item.id}>
                              <td>{item.image}</td>
                              <td>{item.objects}</td>
                              <td>{new Date(item.timestamp).toLocaleDateString()}</td>
                              <td>{getStatusBadge(item.status)}</td>
                            </tr>
                          ))}
                        </tbody>
                      </Table>
                    ) : (
                      <p className="text-muted text-center my-4">No recent activity</p>
                    )}
                  </Card.Body>
                </Card>
              </Col>
            </Row>

            {/* Uploaded Image + Result */}
            {selectedFile && (
              <Row className="mb-4">
                <Col>
                  <Card className="shadow-sm text-center p-3">
                    <h5>Selected File:</h5>
                    <p>{selectedFile.name}</p>
                    {outputUrl && (
                      <div>
                        <h6>Output Preview:</h6>
                        <img
                          src={`http://localhost:8000/${outputUrl}`}
                          alt="Output"
                          style={{
                            maxWidth: '70%',
                            borderRadius: '10px',
                            marginTop: '10px',
                          }}
                        />
                      </div>
                    )}
                  </Card>
                </Col>
              </Row>
            )}
          </motion.div>
        </Container>
      </div>
    </div>
  );
};

export default DashboardPage;
