import React, { useState, useRef } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Form,
  ProgressBar,
  Image,
} from "react-bootstrap";
import { motion } from "framer-motion";
import {
  CloudUpload,
  XCircle,
  EyeFill,
} from "react-bootstrap-icons";
import axios from "axios";
import { useNotifications } from "../contexts/NotificationContext";

const DetectionPage = () => {
  const [images, setImages] = useState([]);
  const [results, setResults] = useState([]);
  const [isDetecting, setIsDetecting] = useState(false);
  const [progress, setProgress] = useState(0);
  const [showConfidence, setShowConfidence] = useState(true);
  const [previewUrls, setPreviewUrls] = useState([]);
  const fileInputRef = useRef(null);
  const { addNotification } = useNotifications();

  // ‚úÖ File Selection
  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    if (files.length > 0) {
      const newPreviewUrls = files.map((file) => URL.createObjectURL(file));
      setPreviewUrls((prev) => [...prev, ...newPreviewUrls]);
      const newImages = files.map((file) => ({
        file,
        name: file.name,
        size: file.size,
      }));
      setImages((prev) => [...prev, ...newImages]);
    }
  };

  // ‚úÖ Remove selected image
  const removeImage = (indexToRemove) => {
    setImages((prev) => prev.filter((_, index) => index !== indexToRemove));
    setPreviewUrls((prev) => prev.filter((_, index) => index !== indexToRemove));
  };

  // ‚úÖ Run Object Detection
  const handleDetect = async () => {
    if (images.length === 0) {
      addNotification("error", "Please upload at least one image.");
      return;
    }

    setIsDetecting(true);
    setProgress(0);
    setResults([]);

    const formData = new FormData();
    images.forEach((img) => formData.append("image", img.file));

    try {
      const res = await axios.post("http://localhost:8000/detect-batch", formData, {
        headers: { "Content-Type": "multipart/form-data" },
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round(
            (progressEvent.loaded * 100) / progressEvent.total
          );
          setProgress(percentCompleted);
        },
      });

      const data = res.data;
      setResults(data.results || []);
      addNotification("success", "‚úÖ Object detection completed successfully!");
    } catch (err) {
      console.error("Detection Error:", err);
      addNotification("error", "‚ùå Error during detection.");
    }

    setIsDetecting(false);
  };

  // ‚úÖ Drag-Drop handlers
  const handleDragOver = (e) => e.preventDefault();
  const handleDrop = (e) => {
    e.preventDefault();
    const files = Array.from(e.dataTransfer.files).filter((f) =>
      f.type.startsWith("image/")
    );
    const newPreviewUrls = files.map((file) => URL.createObjectURL(file));
    setPreviewUrls((prev) => [...prev, ...newPreviewUrls]);
    const newImages = files.map((file) => ({
      file,
      name: file.name,
      size: file.size,
    }));
    setImages((prev) => [...prev, ...newImages]);
  };

  return (
    <div
      style={{
        display: "flex",
        height: "100vh",
        overflow: "hidden",
        backgroundColor: "#f9fafb",
      }}
    >
      {/* ===== Sidebar (20%) ===== */}
      <div
        style={{
          width: "20%",
          background: "linear-gradient(135deg, #111827, #1f2937)",
          color: "white",
          display: "flex",
          flexDirection: "column",
          justifyContent: "space-between",
          padding: "30px 20px",
        }}
      >
        <div>
          <h3 className="fw-bold text-center mb-4">VisionAI Tools</h3>
          <Button
            variant="primary"
            className="w-100 mb-3"
            onClick={() => fileInputRef.current.click()}
          >
            üìÅ Upload Images
          </Button>
          <Button
            variant="success"
            className="w-100 mb-3"
            onClick={handleDetect}
            disabled={isDetecting}
          >
            {isDetecting ? "Detecting..." : "üîç Detect Objects"}
          </Button>
          <Button
            variant="info"
            className="w-100 mb-3"
            onClick={() => alert("3D Scene API placeholder")}
          >
            üåç Generate 3D Scene
          </Button>
        </div>
        <Button
          variant="danger"
          className="w-100"
          onClick={() => window.location.assign("/login")}
        >
          üö™ Logout
        </Button>
      </div>

      {/* ===== Main Content (80%) ===== */}
      <div
        style={{
          width: "80%",
          overflowY: "auto",
          padding: "40px",
        }}
      >
        <Container fluid>
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            <Row className="mb-4">
              <Col>
                <h1 className="fw-bold">Object Detection</h1>
                <p className="text-muted">
                  Upload images to detect and analyze objects using AI
                </p>
              </Col>
            </Row>

            {/* Upload Section */}
            <Row className="mb-4">
              <Col>
                <Card>
                  <Card.Body>
                    <div
                      className="upload-area text-center p-5"
                      onDragOver={handleDragOver}
                      onDrop={handleDrop}
                      style={{
                        border: "2px dashed #6b7280",
                        borderRadius: "10px",
                        background: "#f3f4f6",
                        cursor: "pointer",
                      }}
                    >
                      <CloudUpload size={60} className="text-primary mb-3" />
                      <h5>Drag & Drop your images here</h5>
                      <p className="text-muted mb-3">or</p>
                      <Button
                        variant="outline-primary"
                        onClick={() => fileInputRef.current.click()}
                      >
                        Browse Files
                      </Button>
                      <p className="text-muted mt-2">
                        Supports JPG, PNG, JPEG (Max 10MB each)
                      </p>
                    </div>
                    <Form.Control
                      type="file"
                      ref={fileInputRef}
                      onChange={handleFileChange}
                      multiple
                      accept="image/*"
                      className="d-none"
                    />
                  </Card.Body>
                </Card>
              </Col>
            </Row>

            {/* Selected Images Preview */}
            {images.length > 0 && (
              <Row className="mb-4">
                <Col>
                  <h5>Selected Images ({images.length})</h5>
                  <div className="d-flex flex-wrap gap-3">
                    {images.map((img, index) => (
                      <div key={index} className="position-relative">
                        <Image
                          src={previewUrls[index]}
                          alt="preview"
                          thumbnail
                          style={{
                            width: "120px",
                            height: "120px",
                            objectFit: "cover",
                          }}
                        />
                        <Button
                          variant="danger"
                          size="sm"
                          className="position-absolute top-0 end-0"
                          onClick={() => removeImage(index)}
                        >
                          <XCircle />
                        </Button>
                        <div className="text-center mt-1">
                          <small className="text-muted">{img.name}</small>
                        </div>
                      </div>
                    ))}
                  </div>
                </Col>
              </Row>
            )}

            {/* Detection Controls */}
            <Row className="mb-4">
              <Col md={6}>
                <Button
                  variant="primary"
                  size="lg"
                  className="w-100"
                  onClick={handleDetect}
                  disabled={isDetecting || images.length === 0}
                >
                  {isDetecting ? (
                    <span>Processing... {progress}%</span>
                  ) : (
                    <>
                      <EyeFill className="me-2" />
                      Detect Objects
                    </>
                  )}
                </Button>
              </Col>
              <Col md={6} className="d-flex align-items-center">
                <Form.Check
                  type="switch"
                  id="show-confidence"
                  label="Show Confidence Score"
                  checked={showConfidence}
                  onChange={() => setShowConfidence(!showConfidence)}
                />
              </Col>
            </Row>

            {/* Progress Bar */}
            {isDetecting && (
              <Row className="mb-4">
                <Col>
                  <ProgressBar
                    now={progress}
                    label={`${progress}%`}
                    animated
                    striped
                    variant="primary"
                  />
                </Col>
              </Row>
            )}

            {/* Results */}
            {results.length > 0 && (
              <Row>
                <Col>
                  <h5 className="mb-3">Detection Results</h5>
                  {results.map((res, index) => (
                    <Card key={index} className="mb-4 shadow-sm">
                      <Card.Body>
                        <h6>Result #{index + 1}</h6>
                        <Image
                          src={res.processedImage || previewUrls[index]}
                          fluid
                          style={{
                            maxHeight: "400px",
                            objectFit: "contain",
                            borderRadius: "10px",
                          }}
                        />
                        {res.detectedObjects && (
                          <div className="mt-3">
                            <h6>Detected Objects:</h6>
                            <ul className="list-group list-group-flush">
                              {res.detectedObjects.map((obj, i) => (
                                <li
                                  key={i}
                                  className="list-group-item d-flex justify-content-between align-items-center"
                                >
                                  <span>
                                    <span className="badge bg-primary me-2">
                                      {obj.label}
                                    </span>
                                    {obj.bbox && (
                                      <small className="text-muted">
                                        Box: [{obj.bbox.join(", ")}]
                                      </small>
                                    )}
                                  </span>
                                  {showConfidence && (
                                    <span className="badge bg-success">
                                      {(obj.confidence * 100).toFixed(1)}%
                                    </span>
                                  )}
                                </li>
                              ))}
                            </ul>
                          </div>
                        )}
                      </Card.Body>
                    </Card>
                  ))}
                </Col>
              </Row>
            )}
          </motion.div>
        </Container>
      </div>
    </div>
  );
};

export default DetectionPage;
