import React, { useState, useEffect } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  Table,
  ProgressBar,
  Badge,
  Button,
  Nav,
} from "react-bootstrap";
import { motion } from "framer-motion";
import {
  BarChartFill,
  ImageFill,
  Clock,
  GraphUp,
  CheckCircle,
} from "react-bootstrap-icons";
import { useAuth } from "../contexts/AuthContext";
import { useNavigate } from "react-router-dom";

const DashboardPage = () => {
  const { currentUser, isAuthenticated, loading, logout } = useAuth();
  const navigate = useNavigate();

  const [dashboardData, setDashboardData] = useState({
    totalDetections: 0,
    detectionHistory: [],
    topLabels: [],
  });

  const [selectedFile, setSelectedFile] = useState(null);
  const [outputUrl, setOutputUrl] = useState("");
  const [processing, setProcessing] = useState(false);

  // Redirect if not logged in
  useEffect(() => {
    if (!loading && !isAuthenticated) {
      navigate("/login");
    }
  }, [loading, isAuthenticated, navigate]);

  // Mock Dashboard Data
  useEffect(() => {
    const fetchDashboardData = async () => {
      await new Promise((resolve) => setTimeout(resolve, 400));
      setDashboardData({
        totalDetections: 42,
        detectionHistory: [
          {
            id: 1,
            image: "room1.jpg",
            objects: 4,
            timestamp: "2025-10-07T12:30:00Z",
            status: "completed",
          },
          {
            id: 2,
            image: "bathroom.jpg",
            objects: 3,
            timestamp: "2025-10-06T10:00:00Z",
            status: "completed",
          },
        ],
        topLabels: [
          { label: "person", count: 28 },
          { label: "car", count: 15 },
          { label: "dog", count: 8 },
          { label: "bicycle", count: 6 },
        ],
      });
    };
    fetchDashboardData();
  }, []);

  const handleFileChange = (e) => setSelectedFile(e.target.files[0]);

  const handleDetectBatch = async () => {
    if (!selectedFile) return alert("Please select an image first!");
    setProcessing(true);
    const formData = new FormData();
    formData.append("image", selectedFile);

    try {
      const res = await fetch("http://localhost:8000/detect-batch", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      setOutputUrl(data.output_url || "");
      alert("âœ… Object detection complete!");
    } catch (err) {
      console.error("Detection API Error:", err);
      alert("âŒ Error in detection API");
    }
    setProcessing(false);
  };

  const handle3DSceneMap = async () => {
    if (!selectedFile) return alert("Please select an image first!");
    setProcessing(true);
    const formData = new FormData();
    formData.append("image", selectedFile);

    try {
      const res = await fetch("http://localhost:8000/3d-scene-map", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      setOutputUrl(data.output_url || "");
