import React, { useState, useRef } from 'react';
import { Container, Row, Col, Card, Button, Form, Alert, ProgressBar, Image } from 'react-bootstrap';
import { motion } from 'framer-motion';
import { CloudUpload, CheckCircle, XCircle, EyeFill, ToggleOn, ToggleOff } from 'react-bootstrap-icons';
import axios from 'axios';
import { useNotifications } from '../contexts/NotificationContext';

const DetectionPage = () => {
  const [images, setImages] = useState([]);
  const [results, setResults] = useState([]);
  const [isDetecting, setIsDetecting] = useState(false);
  const [progress, setProgress] = useState(0);
  const [showConfidence, setShowConfidence] = useState(true);
  const [previewUrls, setPreviewUrls] = useState([]);
  const fileInputRef = useRef(null);
  const { addNotification } = useNotifications();

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    if (files.length > 0) {
      const newPreviewUrls = files.map(file => URL.createObjectURL(file));
      setPreviewUrls(prev => [...prev, ...newPreviewUrls]);

      const newImages = files.map(file => ({
        file,
        name: file.name,
        size: file.size
      }));

      setImages(prev => [...prev, ...newImages]);
    }
  };

  const removeImage = (indexToRemove) => {
    setImages(prev => prev.filter((_, index) => index !== indexToRemove));
    setPreviewUrls(prev => prev.filter((_, index) => index !== indexToRemove));
  };

  const simulateDetection = async () => {
    if (images.length === 0) {
      addNotification('error', 'Please upload at least one image');
      return;
    }

    setIsDetecting(true);
    setProgress(0);
    setResults([]);

    // Simulate progress
    const interval = setInterval(() => {
      setProgress(prev => {
        if (prev >= 100) {
          clearInterval(interval);
          return 100;
        }
        return prev + 10;
      });
    }, 300);

    try {
      // In a real app, we would call the API
      // const formData = new FormData();
      // images.forEach((image, index) => {
      //   formData.append('images', image.file);
      // });
      // 
      // const response = await axios.post('/api/detect-batch', formData, {
      //   headers: {
      //     'Content-Type': 'multipart/form-data'
      //   }
      // });

      // Simulate API response
      await new Promise(resolve => setTimeout(resolve, 2000));

      const simulatedResults = images.map((image, index) => ({
        id: Date.now() + index,
        originalImage: previewUrls[index],
        detectedObjects: [
          { label: 'person', confidence: 0.92, bbox: [50, 50, 200, 300] },
          { label: 'car', confidence: 0.87, bbox: [250, 100, 350, 200] },
          { label: 'bicycle', confidence: 0.76, bbox: [100, 150, 180, 280] }
        ],
        processedImage: previewUrls[index]
      }));

      setResults(simulatedResults);
      addNotification('success', `${images.length} image(s) processed successfully!`);
    } catch (error) {
      addNotification('error', 'Detection failed. Please try again.');
    } finally {
      clearInterval(interval);
      setIsDetecting(false);
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const files = Array.from(e.dataTransfer.files);
    const imageFiles = files.filter(file => file.type.startsWith('image/'));

    if (imageFiles.length > 0) {
      const newPreviewUrls = imageFiles.map(file => URL.createObjectURL(file));
      setPreviewUrls(prev => [...prev, ...newPreviewUrls]);

      const newImages = imageFiles.map(file => ({
        file,
        name: file.name,
        size: file.size
      }));

      setImages(prev => [...prev, ...newImages]);
    }
  };

  return (
    <Container>
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <Row className="mb-4">
          <Col>
            <h1 className="fw-bold">Object Detection</h1>
            <p className="text-muted">Upload images to detect and analyze objects using AI</p>
          </Col>
        </Row>

        {/* Upload Section */}
        <Row className="mb-4">
          <Col>
            <Card>
              <Card.Body>
                <div 
                  className="upload-area"
                  onDragOver={handleDragOver}
                  onDrop={handleDrop}
                >
                  <CloudUpload size={60} className="text-primary mb-3" />
                  <h5>Drag & Drop your images here</h5>
                  <p className="text-muted mb-3">or</p>
                  <Button 
                    variant="outline-primary" 
                    onClick={() => fileInputRef.current.click()}
                  >
                    Browse Files
                  </Button>
                  <p className="text-muted mt-2">
                    Supports JPG, PNG, JPEG (Max 10MB each)
                  </p>
                </div>
                <Form.Control
                  type="file"
                  ref={fileInputRef}
                  onChange={handleFileChange}
                  multiple
                  accept="image/*"
                  className="d-none"
                />
              </Card.Body>
            </Card>
          </Col>
        </Row>

        {/* Uploaded Images Preview */}
        {images.length > 0 && (
          <Row className="mb-4">
            <Col>
              <h5>Selected Images ({images.length})</h5>
              <div className="d-flex flex-wrap gap-3">
                {images.map((image, index) => (
                  <div key={index} className="position-relative">
                    <Image 
                      src={previewUrls[index]} 
                      alt={`Preview ${index}`}
                      thumbnail
                      style={{ width: '120px', height: '120px', objectFit: 'cover' }}
                    />
                    <Button
                      variant="danger"
                      size="sm"
                      className="position-absolute top-0 end-0"
                      onClick={() => removeImage(index)}
                    >
                      <XCircle />
                    </Button>
                    <div className="text-center mt-1">
                      <small className="text-muted">{image.name}</small>
                    </div>
                  </div>
                ))}
              </div>
            </Col>
          </Row>
        )}

        {/* Detection Controls */}
        <Row className="mb-4">
          <Col md={6}>
            <Button 
              variant="primary" 
              size="lg" 
              onClick={simulateDetection}
              disabled={images.length === 0 || isDetecting}
              className="w-100 btn-animated"
            >
              {isDetecting ? (
                <span>Processing... {progress}%</span>
              ) : (
                <>
                  <EyeFill className="me-2" />
                  Detect Objects
                </>
              )}
            </Button>
          </Col>
          <Col md={6} className="d-flex align-items-center">
            <Form.Check
              type="switch"
              id="show-confidence"
              label="Show Confidence Score"
              checked={showConfidence}
              onChange={() => setShowConfidence(!showConfidence)}
            />
          </Col>
        </Row>

        {/* Progress Bar */}
        {isDetecting && (
          <Row className="mb-4">
            <Col>
              <ProgressBar 
                now={progress} 
                label={`${progress}%`} 
                animated 
                striped 
                variant="primary"
              />
            </Col>
          </Row>
        )}

        {/* Results Section */}
        {results.length > 0 && (
          <Row>
            <Col>
              <h5 className="mb-4">Detection Results</h5>
              {results.map((result, index) => (
                <Card key={result.id} className="mb-4">
                  <Card.Body>
                    <h6>Result #{index + 1}</h6>
                    <Image 
                      src={result.originalImage} 
                      alt="Detection result" 
                      fluid 
                      style={{ maxHeight: '400px', objectFit: 'contain' }}
                    />
                    <div className="mt-3">
                      <h6>Detected Objects:</h6>
                      <ul className="list-group list-group-flush">
                        {result.detectedObjects.map((obj, objIndex) => (
                          <li key={objIndex} className="list-group-item d-flex justify-content-between align-items-center">
                            <span>
                              <span className="badge bg-primary me-2">{obj.label}</span>
                              {obj.bbox && (
                                <small className="text-muted">
                                  Box: [{obj.bbox[0]}, {obj.bbox[1]}, {obj.bbox[2]}, {obj.bbox[3]}]
                                </small>
                              )}
                            </span>
                            {showConfidence && (
                              <span className="badge bg-success">
                                {(obj.confidence * 100).toFixed(1)}%
                              </span>
                            )}
                          </li>
                        ))}
                      </ul>
                    </div>
                  </Card.Body>
                </Card>
              ))}
            </Col>
          </Row>
        )}
      </motion.div>
    </Container>
  );
};

export default DetectionPage;
